import React, { Component } from 'react';
import Web3 from 'web3'
import './App.css';
import{FACTORY_ADDRESS, FACTORY_ABI, TFG_ABI} from './config'
//var BigNumber = require('big-number');
import ExchangesC from './Exchanges'

class Apprueba extends Component {

  constructor(props) {
    super(props)
    this.state = { 
      account: '',
      valuenewexchange:0,
      DataProviders: [],
      exchangedata: '',
      textboxaddress:'',
      addrescontract: '',
      exchangesCounter: 0,
      loading: true
       }
    this.Createnewexchange = this.Createnewexchange.bind(this);
    this.handleChangeexchange = this.handleChangeexchange.bind(this);
    this.SetMR = this.SetMR.bind(this);
    this.confirm = this.confirm.bind(this);
    this.seedReveal = this.seedReveal.bind(this);
    this.handleChangeaddress = this.handleChangeaddress.bind(this);
    this.handleSubmitaddress = this.handleSubmitaddress.bind(this);
  }

  componentWillMount(){
    this.loadBlockchainData()
  }

  async loadBlockchainData(){
    const web3= new Web3(Web3.givenProvider)//podria ser el de infura : "https://mainnet.infura.io/v3/7f5338a0ad3f4479927a4578bfaf0b35"
   // pero entonces no coge las accountsdel metamask
   this.setState({web3})
   if (window.ethereum) {
        window.ethereum.enable()
    }

    const accounts = await web3.eth.getAccounts()
    this.setState({account: accounts[0]})


    const factory= new web3.eth.Contract ( FACTORY_ABI,FACTORY_ADDRESS)
    this.setState({factory})



    //let TFG= new web3.eth.Contract ( TFG_ABI, '0xA6b9FA6336Bbe5280E9492aa1Da318a33ecB3Bd3')


    const exchangesCounter = await factory.methods.exchangesCounter().call()

    this.setState({exchangesCounter});


    // this.setState({exchangedatas: []});
    // for (var i = 1; i <= exchangesCounter; i++) {
    //   const exchange = await factory.methods.exchangedatas(i).call()
    //   console.log("exchange: ",i, exchange)
    //   this.setState({
    //     exchangedatas: [...this.state.exchangedatas, exchange]
    //   })
    // }


    console.log(this.state.account)


   this.setState({loading: false})

  }



  showLoading(){
    return  <div id="loader" className="text-center">
    <p className="text-center">Loading...</p>
  </div>

  }


  handleChangeexchange(event) {
    this.setState({valuenewexchange: event.target.value});
  }

  loadExchanges(){

    return  <ExchangesC exchangedatas={this.state.exchangedatas} 
     SetMR= {this.SetMR} confirm= {this.confirm} seedReveal= {this.seedReveal}/>
  }




  async Createnewexchange(){
    let result =await  this.state.factory.methods.createChildContract(this.state.valuenewexchange).send({from: this.state.account});
      console.log("result", result)
    // this.state.factory.methods.createChildContract(this.state.valuenewexchange).send({from: this.state.account}, (error, transactionHash) => {
    //   if (error)
    //     console.log(error)
    //   else{
    //     console.log("hashtransaction", transactionHash);    

    //   }
    //   });
  }

  async SetMR(address){

    let TFG= new this.state.web3.eth.Contract ( TFG_ABI, address)
    TFG.methods.SetMR(100,1000).send({from: this.state.account}, (error, transactionHash,receipt) => {
      if (error)
        console.log(error)
      else{
        console.log("hashtransaction", transactionHash);    
        console.log("receipt", receipt)
      }

      });
  }
  async confirm(address){

    let TFG= new this.state.web3.eth.Contract ( TFG_ABI, address)
    TFG.methods.confirm().send({from: this.state.account}, (error, transactionHash) => {
      if (error)
        console.log(error)
      else{
        console.log("hashtransaction", transactionHash);    
      }

      });
  }
  async seedReveal(address){

    console.log("address seedreveal", address)
    let TFG= new this.state.web3.eth.Contract ( TFG_ABI, address)
    TFG.methods.releaseSeed(10000).send({from: this.state.account}, (error, transactionHash) => {
      if (error)
        console.log(error)
      else{
        console.log("hashtransaction", transactionHash);    
      }

      });
  }
  handleChangeaddress(event) {
    this.setState({textboxaddress: event.target.value});
  }

  async handleSubmitaddress(event) {
    event.preventDefault();
    let found = false;
    for (var i = 1; i <= this.state.exchangesCounter; i++) {
      const exchange = await this.state.factory.methods.exchangedatas(i).call()
      if (exchange=this.state.textboxaddress)
      {
        found = true
        this.setState({addrescontract: this.state.textboxaddress});
        console.log("address correct, may be better a pop up")
        break;
      }
    }

  }

  render() {
    return (
        <div>
          <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
            <ul className="navbar-nav px-3">
              <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
                <small><a className="nav-link" href="#"><span id="account"></span></a></small>
              </li>
            </ul>
          </nav>
          <div className="container-fluid">
            <div className="row">
              <main role="main" className="col-lg-12 d-flex justify-content-center">

                {this.state.loading? this.showLoading() : this.loadExchanges()}

              </main>
            </div>
          </div> 
          <form >
                    <input id="mewexchange"  type="number" step="1" className="form-control" 
                    value={this.state.valuenewexchange} onChange={this.handleChangeexchange}/>
                    <button type="button" onClick={this.Createnewexchange}>
                     Createnewexchange
                    </button>
          </form>
          <form onSubmit={this.handleSubmitaddress}>
                    <input id="newTask"  type="text" className="form-control" 
                    value={this.state.textboxvalue} onChange={this.handleChangeaddress}/>
                    <input type="submit" hidden={true} />
                  </form>
        </div>
      );
  }
}

export default Apprueba;
