import React, { Component } from 'react';


class Exchanges extends Component {


 constructor(props) {
       super(props)
       this.state = {
        //tasks: [] onmount creo
         textboxvalue: '',
         selected: false

          }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handeclick = this.handeclick.bind(this);
  }
  handleChange(event) {
    this.setState({textboxvalue: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.newRegister(this.state.textboxvalue)

  }
  handeclick(event) {
    this.setState({textboxvalue: event.target.value});
  }

  shownoSelected(){
    return  <div id="noselected" className="text-center">
    <p className="text-center">select one address to proceed</p>
  </div>
  }
  render() {
    return (
         <div id="content">
                  <form onSubmit={this.handleSubmit}>
                    <input id="newTask"  type="text" className="form-control" 
                    value={this.state.textboxvalue} onChange={this.handleChange}/>
                    <input type="submit" hidden={true} />
                  </form>
                  <li id="exchangelist" onClick={this.handeclick} className="list-unstyled">
                    {this.props.exchangedatas.map((exchange, key) => {
                      return(
                        <div key={key}>
                          <label>
                            <span className="content">{key} address: {exchange}</span>
                          </label>
                        </div>
                      )
                    })}
                </li>

                {this.state.selected? this.showLoading() : this.loadExchanges()}


                </div>

      );
  }
}

export default Exchanges;
